export default {
  props: {
    items: {
      type: Array,
      required: true
    }
  },
  computed: {
    currentRefinement() {
      return this.$store.state.search.filters.sortBy;
    }
  },
  methods: {
    refine(value) {
      return this.$store.commit('search/SET_FILTERS_ATTR', {
        key: 'sortBy',
        value
      });
    }
  },
  render() {
    return this.$scopedSlots.default({
      items: this.items,
      currentRefinement: this.currentRefinement,
      refine: this.refine
    });
  }
};
